msg.payload.Timestamp = new Date();
msg.payload.PV_in_watts = msg.payload.PV_in_current * msg.payload.PV_in_voltage;
if(msg.payload.Warnings == '00000000000000000000000000000000') {
    msg.payload.Warnings = null;
    msg.payload.clouds = flow.get("clouds");
}
return msg;

if(msg.payload.Battery_capacity < 50) {
    msg.payload.alert = "Battery running low: " + msg.payload.Battery_capacity + " %";
    msg.payload.reduce_load = true;
}
if(msg.payload.Battery_discharge_current > 35) {
    msg.payload.alert = "Battery discharge current too high";
}
if(msg.payload.Load_watt > 3500) {
    msg.payload.alert = "Output load too high";
    msg.payload.reduce_load = true;
}

return msg;

if(msg.payload.alert || msg.payload.suggested_action) {
    return msg;

}
return null;

var date = new Date();


var sbuStartHour = 5; var sbuStartMin = 0;
var sbuEndHour = 15; var sbuEndMin = 0;

var sbuBatteryUpperLimit = 90;
var sbuBatteryLowerLimit = 50;

if(date.getHours() >= 8) {
    sbuBatteryUpperLimit = 100;
    sbuBatteryLowerLimit = 60;
}

if(date.getHours() >= 9) {
    sbuBatteryUpperLimit = 100;
    sbuBatteryLowerLimit = 70;
}

if(flow.get("sunrise")) {
    var sunrise = new Date(flow.get("sunrise")*1000);
    sbuStartHour = sunrise.getHours() - 3;
    sbuStartMin = sunrise.getMinutes() + 30;
    if(sbuStartMin >= 60) {
        sbuStartMin = sbuStartMin - 60;
        sbuStartHour += 1;
    }
}


var hybrid = msg.payload.Out_source_priority == 2 &&
            (date.getHours() > sbuEndHour || (date.getHours() == sbuEndHour && date.getMinutes() >= sbuEndMin));

var sbu = msg.payload.Out_source_priority == 1 &&
          (date.getHours() > sbuStartHour || (date.getHours() == sbuStartHour && date.getMinutes() >= sbuStartMin)) && 
          (date.getHours() < sbuEndHour || (date.getHours() == sbuEndHour && date.getMinutes() < sbuEndMin)) && 
          msg.payload.Load_watt < 1000 &&
          msg.payload.Battery_capacity >= sbuBatteryUpperLimit && 
          (flow.get("clouds") || flow.get("clouds") === 0) && flow.get("clouds") <= 50;




// if battery discharge current is too high or battery is running low then exit SBU mode
if(msg.payload.Out_source_priority == 2) {
    if(msg.payload.Battery_discharge_current >= 35 || 
        msg.payload.Battery_capacity <= sbuBatteryLowerLimit ||
        flow.get("clouds") > 50) {
        hybrid = true;
    }
}


if(sbu && !hybrid) {
    context.set("sbu", true);
    msg.payload.suggested_action = "SBU mode";
    msg.payload.inverter_command = "POP02";
    node.warn(msg.payload.suggested_action);

} else if(hybrid) {
    context.set("sbu", false);
    msg.payload.suggested_action = "Hybrid mode";
    msg.payload.inverter_command = "POP01";
    node.warn(msg.payload.suggested_action);
}

return msg;

var output;
if(msg.payload.suggested_action && msg.payload.inverter_command) {
    node.warn('Changing Inverter mode to ' + msg.payload.suggested_action + " - Command: " + msg.payload.inverter_command);
    output = {
        payload: msg.payload.inverter_command
    };
}

return output;
